{
    if(msg.data.Status.A.Tr_Th==1){alarms="Over Temperature"}
    if(msg.data.Status.A.Tr_Te==1){alarms="Low Temperature"}
    if(msg.data.Status.A.Tr_DR==1){alarms="Dry Burn Alarm"}
    if(msg.data.Status.A.Tr_CS==1){alarms="Contactor Fault"}
    if(msg.data.Status.A.Tr_PCB==1){alarms="PCB Temperature Hi"}
    if(msg.data.Status.A.A_Sc==1){alarms="Scale Build-up"}
    if(msg.data.Status.A.A_An==1){alarms="Anode Depletion Alarm"}
    if(msg.data.Status.A.A_DT==1){alarms="Driptray Water Alarm"}
    color=1;
}
else
{
    if(msg.data.Status.O.AC==1)
    {
        if(msg.data.Status.T == 1)
        {
            alarms = "Timer Active";
        }
        else
        {
            if(msg.data.Settings.O.ACO==1)
            {
                alarms = "AC Boost Active";
            }
            else
            {
                alarms = "Heating from AC";
            }
        }
    }
    else
    {
        alarms = "Heating from PV";
    }
}

msg.text = "Status: <b>"+alarms+"</b>";
msg.payload = color;
return msg;

